customCSS:
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@700;900&family=Source+Sans+Pro:wght@300;400;600&family=Oswald:wght@500;700&family=JetBrains+Mono&display=swap');

:root {
  --slidev-theme-primary: #B22234;
  --slidev-theme-background: #070E1A;
}

.slidev-layout {
  background: #070E1A;
  color: #F0EDE8;
  font-family: 'Source Sans Pro', sans-serif;
}

h1, h2, h3 {
  font-family: 'Playfair Display', serif;
  color: #FFFFFF;
  line-height: 1.15;
}

.label {
  font-family: 'Oswald', sans-serif;
  text-transform: uppercase;
  letter-spacing: 0.15em;
  color: #B22234;
  font-size: 0.8rem;
}

.accent-line {
  border-left: 4px solid #B22234;
  padding-left: 1rem;
}

.gold { color: #C9A84C; }
.red { color: #B22234; }
.muted { color: #8B9AB5; }
